import UIKit

protocol UserPresentationLogic
{
  func presentData(response: UserScene.LoadingData.Response)
}

class UserPresenter: UserPresentationLogic
{
  weak var viewController: UserDisplayLogic?
  
  // MARK: Do something
  
  func presentData(response: UserScene.LoadingData.Response)
  {
      let viewModel = UserScene.LoadingData.ViewModel(users: response.users)
    viewController?.showData(viewModel: viewModel)
  }
}
