import UIKit

class UserWorker
{
    
    func fetchUsers(completionHandler: @escaping([User]) -> Void, failureCompletion:@escaping(Error?) -> Void) {
        APIManager.shared.getUser(completionHandler: { users in
            completionHandler(users)
        }, failureCompletion: { error in
            failureCompletion(error)
        })
        
    }
}
